const Course = ({ course }) => {
  return (
    <div>
      <Header course={course.name} />
      <Content parts={course.parts} />
      <Total parts={course.parts} />
    </div>
  )
}


const Header = (props) => {
  return (
    <div>
      <h1>{props.course}</h1>
    </div>
  )
}

const Content = (props) => {
  return (
    <>
      {props.parts.map((element, index) => {
        return (
          <div key={index}>
            <Part course={element.name} exercises={element.exercises} />
          </div>
        )
      })}
    </>
  )
}

const Part = (props) => {
  return (
    <div>
      <p>{props.course} {props.exercises}</p>
    </div>
  )
}

const Total = (props) => {
  /*let amount = 0
  props.parts.forEach(element => {
    amount = amount + element.exercises 
  }); */
  //let initial = 0
  let exerciseamounts = props.exercises
  const amount = exerciseamounts.reduce((sum, number) => {
    //console.log()
    return sum + number
  })

  return (
    <div>
      <p>Number of exercises {amount} </p>
    </div>
  )
}

const App = () => {
  const course = {
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id: 3
      }
    ]
  }

  return (
    <div>
      <Course course={course} />
    </div>
  )
}

export default App
