const Header = (props) => {
  return (
    <div>
      <h1>{props.course}</h1>
    </div>
  )
}

const Content = (props) => {
  return (
    <div>
      <Part course={props.part1.name} exercises={props.part1.exercises} />
      <Part course={props.part2.name} exercises={props.part2.exercises} />
      <Part course={props.part3,name} exercises={props.part3.exercises} />
    </div>
  )
}

const Part = (props) => {
  return (
    <div>
      <p>{props.course} {props.exercises}</p>
    </div>
  )
}

const Total = (props) => {
  return (
    <div>
      <p>Number of exercises {props.part1.exercises + props.part2.exercises + props.part3.exercises} </p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  //const part1 = 'Fundamentals of React'
  //const exercises1 = 10
  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  //const part2 = 'Using props to pass data'
  //const exercises2 = 7
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  //const part3 = 'State of a component'
  //const exercises3 = 14
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }

  return (
    <div>
      <Header course={course} />
      <Content part1={part1} part2={part2} part3={part3} exercises1={exercises1} exercises2={exercises2} exercises3={exercises3} />
      <Total part1={part1} part2={part2} part3={part3} />
    </div>
  )
}

export default App
