const Course = ({ courses }) => {
  /*return (
    <div>
      <Header course={course.name} />
      <Content parts={course.parts} />
      <Total parts={course.parts} />
    </div>
  )*/
  return (
    <div>
        {courses.map(course => {course.name})}
    </div>
  )
}

const Header = (props) => {
  return (
    <div>
      <h1>{props.course}</h1>
    </div>
  )
}

const Content = (props) => {
  return (
    <>
      {props.parts.map((element, index) => {
        return (
          <div key={index}>
            <Part course={element.name} exercises={element.exercises} />
          </div>
        )
      })}
    </>
  )
}

const Part = (props) => {
  return (
    <div>
      <p>{props.course} {props.exercises}</p>
    </div>
  )
}

const Total = (props) => {
  /*let amount = 0
  props.parts.forEach(element => {
    amount = amount + element.exercises 
  }); */
  //let initial = 0
  //let exerciseamounts = [props.parts.exercises]
  //console.log(exerciseamounts)

  const initialVal = 0
  const amount = props.parts.reduce((sum, number) => 
    sum + number.exercises, initialVal
  )

  return (
    <div>
      <p>Number of exercises {amount} </p>
    </div>
  )
}

const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1 
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    }, 
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]

  return (
    <div>
      <Course courses={courses} />
    </div>
  )
}

export default App
